INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('LAW', '507', 'Property', 'Ed Mc Caffery', 'The idea of property as understood through economic and philosophical concepts. Rights in land, water and other natural resources. Forms of shared ownership (e.g., landlord and tenant), and a survey of mechanisms for controlling land use.', 2);
