INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '290', 'Introduction to Research Apprenticeship', 'David Glasgow,Elahe Nezami', 'Individual research apprenticeship in health related fields under supervision of a departmental faculty member. Graded CR/NC. Departmental approval.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '300', 'Theoretical Principles of Health Behavior', 'David Black', 2096549, 'Overview and analysis of predictors and consequences of health-related behaviors; theoretical viewpoints and strategies for behavior change. Recommended preparation: HP 200', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '320', 'Biological and Behavioral Basis of Disease', 'Ashley Bennett', 'Examination of the major systems of the human body; disease processes and behavioral risk factors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '340Lg', 'Health Behavior Statistical Methods', 'Farzana Choudhury', 2371275, 'Intermediate statistics for health behavior studies; topics include descriptive statistics, hypothesis testing, correlation and regression, and use of computer software in data analysis.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '350L', 'Health Behavior Research Methods', 'Roksana Karim', 'Introduction to the design, conduct and evaluation of health behavior research studies; quantitative and qualitative approaches to research and analysis. Recommended preparation: HP 340L', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '365mgw', 'Culture, Lifestyle, and Health', 'Sabrina Smiley', 'Comparison of national and international differences in health status as influenced by cultural practices and lifestyles within geographic, economic, and political environments. (Duplicates Credit in former HP-400m)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '402', 'Maternal and Child Health', 'Leslie Clark', 2347676, 'Health issues of women of childbearing age from pre-pregnancy through the postpartum period, and of children from their development in utero through early adolescence. Recommended preparation: PSYC 100', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '405', 'A Global Public Health Priority', 'Panayiota Courelli', 2184122, 'An overview of the magnitude and impact of STDs including prevention, diagnosis, and treatment of common STDs, STD/HIV inter-relationship, global burden, trends, public health challenges, and STD/HIV prevention and control strategies and programs worldwide.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '409', 'Environmental Impacts on the Brain', 'Megan Herting,Shohreh Farzan', 'An examination of the physical and chemical factors in the environment that contribute to neurodevelopmental disorders, cognitive function and neurodegeneration.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '410', 'Issues in Prevention and Cessation of Drug Abuse', 'Steven Sussman', 'Examination of factors related to drug abuse behaviors; overview and assessment of drug abuse prevention and cessation programs; relapse prevention programs. Recommended preparation: HP 300', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '422', 'AIDS in Society', 'Roksana Karim,Joel Milam', 'Provides a broad examination of issues in HIV/AIDS, including behavioral, social, biological, clinical and ethical dimensions of the pandemic in the U.S. and elsewhere.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '431', 'Behavior and Education Strategies for Nutrition and Fitness', '', 'Examination of dietary intake and exercise behaviors as they relate to health and illness; methods for measuring diet and exercise. Recommended preparation: HP 430', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '440', 'Happiness, Well-Being, and Health', 'Joel Milam', 659761, 'Explores human strengths that promote happiness/well-being and whether they influence physical health; mind-body relationships; and strategies for promoting hope, resilience, and quality of life. Recommended preparation: HP 200, PSYC 100', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('HP', '442', 'Chronic Disease Epidemiology', 'Victoria Cortessis', 1888908, 'Overview of causative factors and demographic distributions of the major chronic diseases in the western world; edpidemiologic concepts and research designs. Recommended preparation: HP 320', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '446', 'Poisons, People, and Politics', 'Edward Avol', 'Case studies of toxic exposures and investigation of the role of government, scientists, labor and industry in protecting against health threats caused by toxic exposures.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '448', 'Global Environmental Changes and Health', 'Edward Avol', 'Discussion of global environmental changes, including climate change, air pollution, water pollution, radiation, and their impacts on human health.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '450', 'Traditional Eastern Medicine and Modern Health', 'Sivarama Vinjamury,Jenny Yu', 'Overview of traditional Eastern approaches toward health and disease; relevance to modern health issues, emphasizing a comparison between traditional Chinese and modern Western medicine. Recommended preparation: Fundamentals of Medicine', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '470', 'Case Studies in Global Health', 'Heather Wipfli', 'Case study examination of programs and organizational structure underlying current international efforts addressing problems related to infection disease, chronic disease, global environmental change, emergencies and emerging disease epidemics.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('HP', '490x', 'Directed Research', 'David Glasgow,Elahe Nezami', 'Individual research and readings. Not available for graduate credit. Open to HP majors only. Recommended preparation: HP 350L', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INTD', '522', 'Infection and Host Responses', 'Jie Li', 'Overview of microbes, their life cycles and the host responses they elicit, evade or exploit, including the manipulation and the malfunction of the immune system.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INTD', '551', 'Pathobiology of Disease', 'Louis Dubeau', 'Relationship between histopathological and clinical manifestations of disease and their underlying molecular mechanisms. Topics include inflammatory, developmental, environmental, degenerative, and neoplastic disease processes.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INTD', '574', 'Medical Biology Seminar', 'W Kast', 'Selected topics in systems biology and disease.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INTD', '577', 'Writing in the Biomedical and Biological Sciences', 'Ite Offringa,Robert Maxson', 'Writing instruction for graduate students focusing on grant proposals and scientific papers. Includes both writing and providing critiques of classmates work. Lectures and discussion. Open only to doctoral students in the school of Medicine.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('INTD', '600', 'Student Research Presentation', 'Young-Kwon Hong', 'Students prepare and present their own research to an audience of faculty and peers. Graded CR/NC. Open only to graduate students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '220', 'Preparation for the Clinical Experience', 'Emily Rose', 'Discussion and exercises in clinically relevant topics (professional behavior, patient privacy, medical ethics, cultural competency, teamwork, etc.) in preparation for experience in the clinical setting.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '260', 'Challenges in the Forefront of Biomedical Ethics', 'Cheryl Lew', 'Examination of fundamental ethical concepts and principles in clinical health care and biomedical research. Use of film, case studies and discussion. Recommended preparation: MDA-110 or MEDS-220.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '280', 'a Doctor\'s Perspective', '', 'Explores the role of medicine/surgery in society, whose ideas about health and disease have undergone enormous changes from antiquity to the present day.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '315', 'Human Anatomy, Physiology, and the Technology of Medicine', 'Emily Rose', 'Introduction to human anatomy and physiology in relation to common disease processes, and an examination of how technology has transformed western medicine throughout history.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '320', 'Human Cadaveric Anatomy', 'Michael Habib', 2047192, 'Anatomical structure and function of the major regions of the human body as related to health and disease, using clinical cases, lectures and cadaveric demonstrations.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '340', 'The Brain in Health and Disease', 'Susan Kim', 'Study of the human brain with emphasis on structure-functional relationships, illustrative case studies, current imaging techniques, with illustrative laboratory exercises using specimens and slides. Prerequisites: BISC 220L or BISC 221L; CHEM 103 or CHEM 105a or CHEM 115a. Recommended preparation: MEDS 310.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '350', 'Drugs, Brain, and Behavior', 'Lina Augius', 'The neurochemistry of psychiatric illness and addiction, introduction to brain and nervous system biochemistry, anatomy and disease states. Covers prescribed medications and drugs of abuse.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '370', 'Non-Communicable Chronic Disease', 'Janak Chandrasoma', 'An examination of frequently treated diseases with the greatest impact on society. Involvement of medical specialists and actual patients as guests. Prerequisite: MEDS 220 and 1 from (BISC-220 or BISC-221) and 1 from (CHEM-103 or CHEM-105a or CHEM-115a) . Recommended preparation: MEDS 320 or EXSC 301.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '380', 'Fact and Fiction', 'Gage Crump', 2363425, 'Exploring how stem cells and regenerative medicine are portrayed in culture, the scientific underpinnings of what is currently possible, and visions into the future. Prerequisites: BISC 220L or BISC 221L.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '420', 'Clinical Pediatrics', 'Parul Bhatia,Kira Molas-Torreblanca,Derek Halet', 'Explores enduring and fascinating topics in Pediatrics through a modern lens, incorporating first hand patient-care experiences in clinics and hospitals, and didactic case-based discussions. Prerequisites: BISC 220L or BISC 221L, and MEDS 220.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '425', 'Investigating Death', 'Lakshmanan Sathyavagiswaran', 'Emphasizes the team-based approach used by forensic scientists, medical examiners and coroners to investigate the circumstances and determine the cause/manner of death. Prerequisites: BISC 220 or BISC 221 and MEDS 220. Open only to sophomores, juniors, and seniors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '450', 'The Medicine and Surgery of Reproduction', 'Ramen Chmait,Margaret Purnell', 'An introduction to the practical realities of modern obstetric and gynecology practice from the point of view of practicing surgeon scientists at USC. Prerequisites: BISC-220 or BISC-221 and MEDS-220.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '455', 'Integrative Health Care', '', 'Introduction to integrative health care; a holistic approach to medicine combining conventional medical therapies with evidence-informed complementary alternative medicine.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '460', 'Emergency Health Care', '', 'Principles of emergency medical services, ranging from pre-hospital care and advanced life-saving, to minor injuries and illnesses. Interactive experience with ER physicians/emergency health care providers. Prerequisites: BISC 220 or BISC 221 and MEDS 220. Recommended preparation: MEDS 260.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '465', 'Wilderness and Survival Medicine', 'Matthieu De Clerck', 'Medical physiology principles of people under "extreme" wilderness conditions, factors affecting remote medical care, and basic survival strategies, led by Emergency Medicine physicians. Prerequisites: BISC 220 or BISC 221 and MEDS 220.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '490', 'Directed Research in Biomedical Science', 'Joyce Richey', 'Individual research and readings. Recommended preparation: MEDS 300 and MEDS 360. Not available for graduate credit.Prerequisite: BISC 220 or BISC 221) and CHEM 103 or CHEM 105a or CHEM115a.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '502', 'Global Epidemiology of Diseases and Risk Factors', 'Gilda Noori', 2282208, 'Survey of major population crises and risk factors accounting for global health challenges. Evaluation of case studies of global health interventions and effective methodologies.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '511', 'Global Health Modules, Tuberculosis', 'Aninda Das', 2378604, 'Exploration of biological and epidemiological fundamentals of tuberculosis including dynamics of transmission among populations. Overview of clinical manifestations, diagnosis, and treatment of infections.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '512', 'Global Health Modules, Maternal and Child Health', 'Gitanjli Arora', 'Biopsychosocial, cultural and economic aspects of womens health: pregnancy, childbirth, marriage. Examination of health promotion and policy efforts to improve womens health globally.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '518', 'Global Policies and Programs', 'Maryam Farzanegan', 'Focuses on children in emergency situations, including natural and man-made disasters, such as floods, earthquakes, conflicts, or war, with an emphasis on the poorest and most vulnerable children. Open only to Global Medicine majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '520', 'Medical Spanish for the Health Professions', 'David Zarazua', 1611452, 'Spanish language course for students planning to enter the health professions. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '521', 'Emerging and Re-emerging Infectious Diseases', 'Noah Wald-Dickler', 2378608, 'Exploration of the threat of major worldwide epidemics and diseases with a focus on the recent emergence of new plagues.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '523', 'Global Toxicity and Carcinogenesis', '', 'Covers the occurrences of toxic substances and the toxicity/diseases they cause, and chemical carcinogens and the types of cancer they cause worldwide.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '524', 'Grant Writing for Non-Government Organizations', 'Monica Gomez', 'Instruction on the process and methods of writing effective grants from identifying appropriate funders to implementing project upon receipt of funding award.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '525', 'Global Mental Health', '', 'Examines the major mental health diagnoses from clinically relevant perspectives and their prevalence in specific geographical regions around the world.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '528', 'Global Health Modules, Sexually Transmitted Infections', 'Alexandra Portaro', 2378602, 'Examines clinically relevant perspectives from distinguished international authors on STI issues and the devastating effect on particular geographical regions around the world.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '530b', 'Anatomy, Physiology, and Pathology', 'Mansour Rostami', 2378611, 'The basics of human anatomy (gross anatomy, histology, radiographic anatomy), physiology (cellular physiology, organ system areas) and pathology (general, systemic, cellular pathology).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '530c', 'Anatomy, Physiology, and Pathology', 'Mansour Rostami', 2378611, 'Continues material from MEDS-530a and MEDS-530b, covering human anatomy (gross anatomy, histology, radiographic anatomy), physiology (cellular physiology, organ systems) and pathology (general, systemic, cellular pathology).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '567', 'Global Nutrition', 'Roger Clemens', 'A systematic survey of basic and clinical nutrition that provides an overview of critical public health-nutrition issues in both the developing world and industrial society.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '577', 'Global Palliative Care', 'Benjamin La Brot', 'Exploration of the provision of palliative care and analysis of factors affecting the increased burden of palliative care provision facing developing regions of the world.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MEDS', '580', 'Interprofessional Team Dynamics in Healthcare', 'Navid Pour-Ghasemi', 2378635, 'Examination of typical composition and function of interprofessional healthcare teams. Exploration of roles of team members and purview of care issues and common challenges, including medication management, inpatient/outpatient decision making, and care plan implementation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MEDS', '599', 'Special Topics', '', 'Lecture and discussion focused on specific topics within global medicine. Course topic will vary from semester to semester.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MICB', '542', 'Animal Virology', 'Jie Li', 'Virus structure and chemistry; virus-cell interactions; aspects of virus genetics; molecular biology; pathogenesis, immunology, and evolution of viral infections.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MICB', '549', 'Student Seminar Series', 'Axel Schonthal', 'Microbiology students will present research seminars describing their thesis progress.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MICB', '551', 'Procaryotic Molecular Genetics', '', 'Macromolecular processes and their regulation in procaryotics; DNA replication, transcription, and post-transcriptional events in general and as related to operons, phage biology, and eucaryotic organelles.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MICB', '560', 'Recent Advances in Microbiology', 'Axel Schonthal', 'Intensive examination of selected topics in microbiology. Student presentations and critiques. Required of all graduate students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NIIN', '520', 'Experimental Design for Neuroimaging', 'Judy Pa', 'Examine experimental design approaches for experimental and clinical neuroimaging investigation. Topics on how to develop rigorous experiments to test theories of cognitive and clinical neuroscience.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NIIN', '550', 'Computational Modeling in Neuroimaging', 'Yonggang Shi', 'Addresses the current neuroinformatics approaches to large-scale data representations, mining, and visualization in brain imaging. Open only to Neuroimaging and Informatics majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NIIN', '560', 'Brain Architecture and Neuroanatomic Exploratory Techniques', 'Hong Wei Dong,', 'Introduction to basic theories and methodological approaches to neuroanatomy in animal models including histology, microscopy, behavioral investigations, and neuroinformatics.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NIIN', '570', 'Neuroimaging Genetics', 'Meredith Braskie', 'Lectures on human genetics and epigenetics. Discuss how applying these concepts to brain imaging advances our understanding of healthy development and neurological disease. Emphasis on critical thinking applied both to designing neuroimaging genetics studies and to constructively assessing peer work and existing neuroimaging genetics literature.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NIIN', '598', 'Neuroimaging and Informatics External Speaker Seminar Series', 'John Van Horn', 'Reading and discussion of recent papers by the INI speaker of the week and attendance at the speaker\'s seminar. Open only to Neuroimaging and Informatics.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PATH', '554', 'Methods in Molecular Pathology', 'Florence Hofman', 'Theory and practice methods useful in experimental pathology; experimental design; statistical analysis; literature analysis; laboratory and radiation safety. Duplicates credit in former PATH 552b.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PATH', '575', 'Frontiers of Pathology', 'Kinji Asahina', 'Weekly research lectures by leading investigators in the field of homeostatic response to injury such as cell death, inflammation, fibrosis and regeneration.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '502', 'Foundations of Public Health', '', 'Overview of public health concepts and issues, including population health; ethics; health disparities, determinants, and systems of care; global health; policy; health promotion; environmental health.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '503', 'Practice of Public Health', 'Luanne Rohrbach,Jane Steinberg', 'Introduction to the application of strategies and tools for practice in public health, including policy advocacy, leadership and management, program planning and evaluation, health communication.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PM', '504', 'Quality in Health Care', 'Sue Kim', 2158407, 'Examines quality assessment at different levels of the health care system including health plans, physicians, hospital and integrated system performance.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PM', '508', 'Health Service Delivery in the U.S.', 'Sue Kim', 2158407, 'Historical development of the American health care system; determinants of health care utilization; role of health care providers; health policy; public health services; and health care finance.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '510L', 'Principles of Biostatistics', 'Lingyun Ji', 'Concepts of biostatistics; appropriate uses and common misuses of health statistics; practice in the application of statistical procedures; introduction to statistical software including EXCEL, SPSS, nQuery. Laboratory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PM', '511aL', 'Data Analysis', 'Farzana Choudhury', 2371275, 'Major parametric and non-parametric statistical tools used in biomedical research, computer packages including SAS. Includes laboratory. Lecture, 2 hours; laboratory, 2 hours.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '511bL', 'Data Analysis', 'Wendy MacK', 'Statistical methods for analysis of categorical data including dichotomous, ordinal, multinomial and count data, using Stata package. Includes laboratory.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '511cL', 'Data Analysis', 'Kiros Berhane', 'Methods and applications for modeling longitudinal, time-to-event and multi- level data. Includes laboratory using R package. Lecture 2 hrs; laboratory 2 hrs. Prerequisites: PM-511b.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '512', 'Principles of Epidemiology', 'Albert Farias,Xuejuan Jiang', 'Terminology/uses of epidemiology and demography; sources/uses of population data; types of epidemiologic studies; risk assessment; common sources of bias in population studies; principles of screening. Recommended preparation: algebra', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '513', 'Experimental Designs', 'Donald Barkauskas', 'Statistical methods for analysis of various experimental designs. Parametric analysis of variance (ANOVA), repeated measures methods, crossover designs, non-parametric ANOVA.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '515', 'Multivariate Statistics in Health Behavior Research', 'Jimi Huh,Trevor Pickering', 'Advanced training in multivariate statistical techniques involving general linear modeling, mixed modeling and basic latent variables analysis in health behavior research', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '516a', 'Statistical Problem Solving', 'Christianne Lane', 'An overview of the tools used by statisticians for solving statistical problems. Graded CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '517b', 'Research Methods in Epidemiology', 'Roberta McKean-Cowdin', 'Overview of epidemiologic research in cancer. Selected cancer sites will be covered to highlight study design and conduct, exposure assessment, data analysis and interpretation.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '518a', 'Statistical Methods for Epidemiological Studies', 'Wendy MacK', 'Principles and methods used in epidemiology for comparing disease frequencies between groups. Restricted to the analysis of binary outcome variables. Prerequisite: PM 512.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '520L', 'Advanced Statistical Computing', 'Paul Marjoram', 'Techniques for the solution of statistical problems throughout intensive computing; iterative techniques, randomization tests, the bootstrap, Monte Carlo methods. Lecture and required laboratory.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '522b', 'Introduction to the Theory of Statistics', 'Meredith Franklin', 'b: Theory of estimation and testing, inference, analysis of variance, theory of regression. Recommended preparation: college-level calculus and linear algebra.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '523', 'Design of Clinical Studies', 'Cecilia Patino Sutton,Todd Alonzo', 'Design, conduct, and interpretation of results of clinical trials; emphasis on principles affecting structure, size, duration of a trial, and the impact of ethical and practical considerations.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '526', 'Communications in Public Health', 'Nancy Boley Cruz', 'Application of communication theories and methods to community health problems. Includes background assessment, program design, evaluation, social marketing, media advocacy, review of major health campaigns. Recommended preparation: PM 500', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '527', 'Epidemiology of Infectious Disease', 'Wendy Cozen', 'Survey of natural history of infectious disease, methods of disease control and outbreak investigation, and an overview of the epidemiology of injury. Prerequisite: PM-512.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '528', 'Program Design and Evaluation', 'Luanne Rohrbach', 'Core concepts, methods and values of public health program planning and evaluation, including community needs assessment, writing objectives, designing health promotion programs, process and outcome evaluation. Recommended preparation: PM 500', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '533', 'Genetic and Molecular Epidemiology', 'Mariana Stern,Sue Ingles', 'Genetic principles; design and analysis of family studies; introduction to likelihood estimation; segregation and linkage analysis; biomarkers of exposure, susceptibility, and disease; laboratory methods; susceptibility genes; association and linkage disequilibrium.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '542', 'Social Network Analysis', 'Thomas Valente', 'Theory, methods and procedures of network analysis with emphasis on applications to public health programs.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '547', 'Public Health Policy and Politics', 'Michael Cousineau', 'Examination of major policy issues in the US health care delivery system to understand policy options in reforming health care and reducing health care disparities.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PM', '551', 'Statistical Methods in Genome-Wide Association Studies', 'Daniel Stram', 412302, 'Provides epidemiologists with an overview of current statistical problems and approaches in the design and analysis of genome-wide association studies. Prerequisites: PM 511a, PM 512. Co-requisite: PM-522a.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '562', 'Intervention Approaches for Health Promotion and Disease Prevention', 'Kimberly Miller,Britni Belcher', 'Approaches for modifying health behavior in varioius settings and within diverse populations. Emphasis on practical considerations necessary to design and implement interventions with demonstrated effectiveness.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '564', 'Public Health Leadership and Management', 'Rita Burke', 'Introduction to leadership and management concepts, tools, and practices in the context of domestic and global public health and healthcare delivery settings.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '575', 'Statistical Methods in Environmental Epidemiology', 'Duncan Thomas', 'Study designs, exposure-time response, longitudinal, spatial, ecologic correlation and mechanistic models, measurement error interactions, measurement error, public policy implications.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '576', 'Global Health Research and Programs', 'Mellissa Withers', 'Introduction to the core concepts and methods of planning and implementing health-related programs and research in resource-constrained settings.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '577', 'Global Health, Law and Human Rights', 'Sofia Gruskin', 'Highlights the complex interactions between global health, law and human rights, emphasizing the use of human rights in public health thinking and practice. Recommended preparation: MPH Core coursework. Duplicates credit in LAW-707.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('PM', '587', 'Qualitative Research Methods in Public Health', 'Ricky Bluthenthal', 1753801, 'Introduction to common uses of qualitative research methods in public health research and application. Methods include ethnography, participant observation, open-ended interviewing, and focus groups.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '591', 'Machine Learning for the Health Sciences', 'Juan Pablo Lewinger', 'Introduces Masters and Ph.D. students in the Health Sciences to Machine Learning methods and their Biomedical applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('PM', '597', 'Capstone in Public Health', 'Xuejuan Jiang', 'Provides the culminating, integrative learning experience for students enrolled in the Master of Public Health program.', 1);
