INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUHL', '351', 'Western Art Music History II', 'Joanna Demers', 'A study of the musical styles and genres in the late modern and contemporary eras within their historical context. Detailed analysis of selected works.', 4);
