INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUIN', '270', 'Introduction to the Music Industry', 'Paul Young', 1292009, 'A survey of the music business with emphasis on distribution of recorded music, music publishing, performance rights societies, record companies, agents, personal managers and contracts. Open to music majors (B.M. and B.S. degrees) only.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUIN', '272x', 'Basics of the Music Industry', 'Michael Garcia', 2368205, 'Introductory survey of the music business. Topics include: copyright, record companies, contracts, music publishing, performance rights societies, managers, agents, and other artist team/income considerations. Not for major credit for Music Industry Majors. (Duplicates credit in former MUIN 372ax.)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUIN', '280', 'Communications in the Music Industry', 'Loren Medina', 'A comprehensive course on communications specific to the music industry with a focus on press and publicity for artists.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUIN', '360', 'Introduction to Music Law', 'Mark Goldstein', 672786, 'A study of entertainment law with a focus on the music industry.Areas of study include contracts, domestic practices,international practices, copyright protection, trademarks. Prerequisites: MUIN 270.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUIN', '370', 'Music Publishing and Licensing', 'Jeff Brabec,Todd Brabec', 'A contemporary survey of the methods used to monetize music through licensing/media placement, covering music publishing, songwriter agreements, performance rights and licenses for traditional/new media. Prerequisites: MUIN 270 or MUIN 272x.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUIN', '410', 'Marketing, Branding and Strategic Alliances in Music', 'Kevin Lyman', 'An in-depth study of music marketing, non-traditional revenue streams for artists, musicians, and labels including artist endorsements, artist tour sponsorships, digital music programs, music licensing, merchandise, and a primary focus on brand partnership deals in the music space. Prerequisites: MUIN 270 or MUIN 272x.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUIN', '420', 'DIY Music Marketing', 'Trudy Green', 'An exploration of the most current and effective marketing strategies and online branding tools for promoting, monetizing, and sustaining the career of the independent creative artist. Prerequisites: MUIN 270 or MUIN 272x.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUIN', '425', 'Live Music Production and Promotion', 'Michael Garcia', 2368205, 'A survey of the presentation of the live musical experience. Both classical and popular concert presentation will be examined including venue selection, promotion and security.', 4);
