INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MPVA', '540', 'Special Studies in Vocal Literature', 'Lisa Sylvester', 'Art song, concert and operatic repertoire. Emphasis to be determined by the department.', 2);
