INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MPWP', '481', 'Interpretation of Baroque Music', 'Rotem Gilbert', 1675399, 'Repertoire and performance practice in music of the period: style, phrasing, embellishments, dynamics, tempi. Performance in class of solo and ensemble works, instrumental and vocal.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MPWP', '499', 'Special Topics', 'Kristy Morrell', 'Selected topics of current interest.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MPWP', '551', 'Individual Instrument Performance Class III', 'Joseph Pereira', 'Solo and orchestra repertoire, professional preparation, reed making, and other matters appropriate to group study. Intended for M.M. wind and percussion majors.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '102gw', 'World Music', 'Scott Spencer', 2262353, 'Exploration of music and cultures of the world. Engagement with international musicians, global issues, field work and musical diasporas in Los Angeles.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '115gp', 'Western Music as Sounding History', 'Rotem Gilbert', 1675399, 'An introduction to Western art music and culture from the Medieval, Renaissance, Baroque, Classical, Romantic and Modern eras through reading, listening, analyzing and writing about music.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '200mgw', 'Reflections of American Diversity', 'Parmer Fuller', 680780, 'A uniquely American genre, the Broadway musical serves as a catalyst for inquiry into human diversity, cross-culturalism, and significant social and political issues.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '210g', 'Electronic Music and Dance Culture', 'Sean Nye', 2058193, 'The origins and development of EDM and its relatives such as disco, house, techno, rave and electronica, focusing on cultural and technological influences.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUSC', '250mgw', 'The Music of Black Americans', 'Ron Mc Curdy', 'A chronicle of the musical contribution of Africans and African Americans to American society and to the foundations of musical genres and styles throughout the world.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '255', 'Songwriting I', 'Andrea Stolpe', 1623690, 'Development of musical and lyrical skills, composing, listening, analysis, and critiques of popular original music.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '320mgw', 'Hip-hop Music and Culture', 'Sean Nye', 2058193, 'A history of hip-hop music from its inception to the present: its musical processes and styles, as well as attendant social, political and cultural issues.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '355', 'Songwriting II', 'David Poe', 2296037, 'Continuation of Songwriting I; particular emphasis on the analysis of the techniques of important popular songwriters and the application of these techniques to original songs.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUSC', '372g', 'Music, Turmoil and Nationalism', 'Lisa Vest', 'An exploration of musical practices and styles which reflect and shape national identities and which focus on those created in response to political turmoil in many forms.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '422', 'Their Music and Their Times', 'Bill Biersach', 675118, 'Music, lyrics, recordings, production techniques, career strategy, social ramifications, and especially the technological impact of the musical group known as The Beatles.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '423', 'Popular Music of the Sixties and Seventies', 'Bill Biersach', 675118, 'Critical examination of the lyrics, structure, associated mythology, technology, and evolving styles of popular music reflecting the turbulent societal changes during the Sixties and Seventies.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '424', 'Iconic Figures of Popular Music', 'Bill Biersach', 675118, 'Music, life, recordings, and attendant musical, cultural and political influences of a seminal musician or group in 20th or 21st century popular music.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUSC', '455', 'The Performing Songwriter', 'Patrice Rushen', 'Continuation of Songwriting I and II with emphasis on the development of performance skills of original popular music in preparation for songwriting showcases.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('MUSC', '465', 'Music, Television and American Culture', 'Jon Burlingame', 531177, 'An exploration of the social and cultural impact of music written for, popularized by, or exploited by American television from the 1950s through today.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUSC', '475', 'Musicians Health and Wellness', 'William Kanengiser', 'Interactive exploration of injury prevention, body awareness disciplines, effective practice strategies, warm-ups and stretches, performance anxiety, hearing protection, and other pertinent wellness topics for musicians.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('MUSC', '496', 'Careers in Music', 'Christopher Sampson,Trudy Green', 'A study of the practical aspects of the music business, including the history, procedures, standard practices, economics and technologies employed by the music industry. Open to juniors and seniors only.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '405', 'Introduction to Scoring Video Games', 'Garry Schyman', 'Practical composition course covering the unique art, craft, and technology involved in composing and integrating video-game music.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '502', 'Individual Instruction in Advanced Screen Scoring', 'Daniel Carlin,Jon Burlingame', 'Individual instruction and review of students work. Selected composers, orchestrators, filmmakers, and other important industry collaborators offer their insights on scoring and career building.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '503b', 'Advanced Scoring for Motion Pictures and Television', 'Robert Young,Daniel Carlin', 'Scoring principles involved in designing, timing, and composing scores for video projects, including documentaries, animated, and live-action projects. Will also include composing for live recording sessions.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '504b', 'Orchestral Scoring', 'Bruce Broughton', 'Explores the sounds of and notations for the major orchestral instruments; provides instruction on implementation of those instruments in scoring and orchestrating for the screen.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '505b', 'Advanced Game Scoring and Integration', 'Garry Schyman', 'Explores more sophisticated principles and techniques involved in the unique aspects of composing and integrating music for video games.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '506', 'Applied Techniques in Contemporary Scoring', 'Patrick Kirst', 'Explores the most recent artistic and technological innovations in film/television scoring, and includes creating cues from adapted themes.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SCOR', '511b', 'History of Film Scoring', 'Jon Burlingame', 531177, 'Examines the evolution of movie music from 1920s to present. Includes recorded interviews with the most significant film composers and film clips of their work.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '521b', 'Recording, Mixing and Editing for the Screen Composer', 'Richard Mc Ilvery', 'Addresses the need for modern-day screen composers to produce and digitally record, sweeten, mix, and edit much of their own music.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SCOR', '523b', 'Advanced Screen Scoring Technology', 'Patrick Kirst', 'Explores sophisticated principles and applications of technologies for screen scoring, including the use of synthesizers, samplers, and sequencers.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '500', 'Bridge Course', 'Christa Bancroft', 2014331, 'Key concepts from chemistry, biochemistry, genetics, cell biology, metabolism, microbiology, and immunology in an 8 week format. Successful completion of this unique preparatory course is expected to position students for success in the graduate level pathophysiology and pharmacology courses.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '501', 'Pathophysiology for Advanced Nursing Practice', 'Nancy Tkacs', 2269046, 'Advanced physiology/pathophysiology including general principles that apply across the lifespan.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '502', 'Advanced Health Assessment Across the Life Span', 'Benita Walton-Moss', 2269048, 'Development of advanced critical thinking and clinical judgement skills through comprehensive health assessment. Health promotion and health maintenance content is utilized to assess health status and evaluate health risk among individuals and groups.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '503', 'Clinical Management of Adult Patients', 'Theresa Granger', 2269050, 'The scope of practice and responsibilities of the family nurse practitioner in prevention of disease, health maintenance, and the diagnosis and management of patients with common illnesses are explored.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '504', 'Pharmacology for Advanced Practice Nursing', 'Nilu Patel', 'Advanced pharmacology including pharmacodynamics, pharmacokinetics, and pharmacotherapeutics of all broad categories of agents.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '505', 'Management of Adult Patients', 'Melissa Jones-Holley', 'Primary health care to patients throughout the life cycle with a focus on the prevention of disease, health maintenance, and the diagnosis and management of patients with common illnesses.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '507', 'Clinical Management of the Childbearing/Childrearing', 'Sharon O\'Neill', 'Diagnosing and managing childbearing women from preconception through uncomplicated pregnancy and postpartum periods and providing primary care to children and their families from newborn through adolescence periods.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '600', 'Clinical Management of Adult Patients with Complex M', 'Kathleen Becker', 2269049, 'The scope of practice and responsibilities of the family nurse practitioner in the diagnosis and management of patients with acute, complex and chronic illnesses are explored.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '601', 'Management of the Childbearing/Childrear', 'Kathleen Porter', 'Clinical practicum is intended to prepare FNP students to provide primary health care to childbearing/childrearing families from preconception through adolescent phase of the life cycle.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '602', 'Research/Analytical Methods', 'Joan Kub', 'Understanding and applying commonly used research methodologies and data analysis techniques in healthcare research.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '603', 'Transforming Research Evidence into Practice', 'Joan Kub', 'Preparing the advanced practice nursing student to critically evalute knowledge, research and evidence for implementation of best practices in healthcare in order to deliver safe, ethical, culturally senstive evidence based care for patients in diverse settings.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('NURS', '604', 'Management of Adult Patients with Comple', 'Kathleen Woodruff', 2269047, 'Application of theoretical concepts studied in NURS 600. Comprehsive assessments, formulation of differential diagnoses, and the development of plans of care to manage acute and chronic complex in a variety of practice settings.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '605', 'Professional Issues in Advanced Practice Nursing', 'Josephine Fava Hochuli', 'The professional role and legal responsibilities of the nurse practitioner at entry to clinical practice and throughout a career are explored.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '606', 'Health Policy Principles in Changing Health Care Contexts', 'Sharon O\'Neill', 'Foundation for leadership in interprofessional collaborative endeavors to address health policy that prepares learners to analyze and influence health policy and explores the role of nurse practitioners in the creation and modification of health policy.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '607', 'Family Primary Care', 'Michelle Zappas', 'Capstone course draws on material and concepts learning throughout the FNP program and explores additional complex issues including management of pain, caregiver burden, palliative care, and end of life.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('NURS', '608', 'Family Primary Care', 'Sharon O\'Neill', 'Prepares the family nurse practitioner student for entry into practice in the diagnosis and managment of patients with acute, complex, and chronic illnesses in the primary care setting.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '101', 'Engaging in Healthy Habits and Routines', 'Ashley Uyeshiro', 'Application of occupational science evidence and occupational therapy principles of lifestyle and self-care behavior change to support self-analysis and integration of healthy habits and routines.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '200L', 'Medical Terminology for Health Professions', 'Myka Winder', 2258025, 'Foundation of medical terminology and hospital abbreviations useful for practice in health care.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '220', 'Introduction to Occupational Therapy', 'Kimberly Morris', 1492408, 'Introduction to theoretical concepts concerning the relationship of engagement in activities (occupations) to health and well being. Application of these perspectives to students\' own lives.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '250', 'Introduction to Occupational Science and Occupational Therapy', 'Kate Crowley', 1269616, 'Introduction to concept of occupation and overview of human drive for meaningful activity; impact of occupations on health and well-being; analysis of personal occupational patterns; selected therapeutic applications.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '251', 'Occupations, Health and Disability', 'Linsey Smith', 'Exploration of the transformative power of occupation throughout the lifespan for all individuals.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '260', 'Human Functional Anatomy for Allied Health Professions', 'Myka Winder,K Michael Rowley', 'Lecture and cadaver lab-based, human anatomy course focused on the interaction of anatomical systems relative to functional activity, health and wellness.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '261', 'Human Physiology for Allied Health Professions', 'Jesus Dominguez', 2260069, 'Overview of human physiology emphasizing systems that sustain homeostasis and motion with a focus on biological foundations for a healthy lifestyle. Relevant pathophysiologic issues discussed.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '280', 'Essential Occupations of Emerging Adulthood', 'Kimberly Morris', 1492408, 'Examination of challenges associated with the emerging adulthood stage of development through an occupational science lens; strategies to promote health and well-being for this population.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '299', 'Special Topics', 'Ashley Uyeshiro,Jesus Diaz,Kate Crowley,Linsey Smith', 'Recent developments in occupational therapy and occupational science for undergraduate students.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '310', 'Creativity Workshop', 'Kimberly Morris', 1492408, 'Theories and practice of the creative process in varied media, genres and occupations. Explores creativity in the arts, sciences, professions, evolution, daily life, and culture.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '325', 'Mind, Body, and Self', 'Kate Crowley', 1269616, 'Exploration of neuroscience as it impacts everyday living, from the fundamentals of neurons and synapses, to the neural basis of language, empathy, and social interaction.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '350', 'Disability, Occupations, and the Health Care System', 'Kate Crowley,Kristy Payne', 'Exploration of the ways in which able-bodyism, sexism, racism, classism and homophobia contribute to occupational opportunities or barriers and weave their way into health care.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '355', 'Occupational Reconstructions and Social Transformations', 'Camille Dieterle', 'The use of occupations -- meaningful, purposeful activities -- to restore identity, agency, health, well-being, skills, and political power to populations in problematic situations, such as wars and natural disasters.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '370', 'Participation Across the Lifespan', 'Linsey Smith', 'Introduction to Autism Spectrum Disorder (ASD) from a neurodevelopmental perspective, with a focus on the daily living experience and occupational participation for individuals with ASD.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '406L', 'Creativity, Craft and Activity Analysis', 'Camille Dieterle', 'Exploration of craftsmanship in both historical and contemporary contexts; engagement in the craft experience to encourage creativity and to develop proficiency in analysis of performance. Open only to upper division, Master and Professional OT majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '441L', 'Neuroscience', 'Myka Winder', 2258025, 'Application of basic neural function to the analysis of daily living tasks and activities; review of pathological conditions that interfere with performance in occupation. Open only to upper division and Master\'s Occupational Therapy majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '500a', 'Clinical Problems in Occupational Therapy', 'Rebecca Cunningham', 'Specific applications of occupational therapy practice in varied clinical/health settings. Seminar to integrate theory with application of treatment principles for various dysfunctional conditions. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '501L', 'Adult Physical Rehabilitation', 'Samia Rafeedie', 1611347, 'Scientific and theoretical underpinnings and knowledge and skills necessary for occupational therapy evaluation and intervention in adult rehabilitation and geriatrics; incorporates related Level I Fieldwork. Duplicates credit in former OT 401. Open only to Occupational Therapy majors.', 8);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '502L', 'Mental Health', 'Deborah Pitts', 655656, 'Scientific and theoretical underpinnings and knowledge and skills necessary for occupational therapy evaluation and intervention in mental health practice; incorporates related Level I Fieldwork. Duplicates credit in former OT 402. Open only to Occupational Therapy majors.', 8);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '503L', 'Pediatrics', 'Karrie Kingsley', 'Scientific and theoretical underpinnings and knowledge and skills necessary for occupational therapy evaluation and intervention in pediatrics; incorporates related Level I Fieldwork. Duplicates credit in former OT 403. Open only to Occupational Therapy majors.', 8);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '521', 'Clinical Reasoning', '', 'Dynamic look at creation and application of professional knowledge and expertise; examines interrelationship between theory and practice; incorporates narrative and illness experiences in clinical reasoning. Duplicates credit in former OT 411. Open only to Occupational Therapy majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '523', 'Communication Skills for Effective Practice', '', 'Principles of written, verbal and non-verbal communication with professionals, clients and families, from a personal and professional perspective; group interventions; interview skills; advocacy as communication. Duplicates credit in former OT 430. Open only to Occupational Therapy majors.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '525', 'Qualitative Research for Evidence-Based Practice', '', 'Traditions and methods of qualitative research; development of skills for research design, implementation and dissemination; critique of qualitative research for evidence-based practice and occupational science. Duplicates credit in former OT 470. Open only to Occupational Therapy majors.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '540', 'Leadership Capstone', 'Mary Jordan,Emily Ochi', 'Professional capstone in leadership, advocacy, ethical reasoning, professional behavior, and public policy as it impacts the practice of occupational therapy; independent professional externship. Duplicates credit in former OT 580. Open only to Occupational Therapy majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '545', 'Advanced Seminar in Occupational Science', '', 'Advanced analysis of occupational science concepts including dimensions of occupation and the impact of occupation on health and wellbeing; factors associated with participation in occupation at the individual, community and global levels. Duplicates credit in former OT 585. Open only to Occupational Therapy majors.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '560', 'Contemporary Issues in School-Based Practice', 'Linsey Smith', 'Current issues in school-based occupational therapy evaluation, ongoing assessment and intervention. Topics include successful collaboration in inclusive classrooms and on IFSP and IEP teams. Open only to OT majors. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '561', 'Occupational Therapy in Acute Care', 'Phuong Nguyen', 'Knowledge and skills for occupational therapy practice in acute care settings using a systems-based approach; includes an experiential learning component at Keck Hospital of USC. Open only to Occupational Science and Occupational Therapy students. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '562', 'Advanced Hand Rehabilitation and Certification', 'Janice Rocker', 'Evidence-based and occupation-based practice for hand and upper extremity conditions; application of PAMs as preparatory for occupation. Applicable toward California educational requirements for advanced practice. Corequisite: OT 573. Open only to Occupational Science and Occupational Therapy students. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '563', 'Occupational Therapy in Primary Health Care Environments', 'Samantha Valasek', 'Examines the emerging role of occupational therapy in primary care, including the need occupational therapy can address in primary care. Open only to Occupational Science and Occupational Therapy majors. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '564', 'Sensory Processing and Sensory Integration', 'Erna Blanche', 'Comprehensive overview of the basic neurobehavioral principles underlying sensory processing and sensory integration, patterns of dysfunction, and their relationship to everyday occupations and intervention.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '565', 'Sensory Integration Interventions', 'Janet Gunter,Erna Blanche', 'Foundations and application of sensory integration principles in clinical practice, schools and community. Includes critique and integration of evidence, effective communication with parents, and documentation.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '566', 'Healthcare Communication with Spanish-Speaking Clients', 'Jenny Martinez', 'Development of communicative proficiency and accuracy in Spanish within a therapeutic context. Introduction to Spanish language, adaptive communication strategies, and ethics in language-discordant communication.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '567', 'Occupational Therapy in Early Intervent', 'Karrie Kingsley', 'Exploration of occupational therapy in early intervention settings including; relevant legislation, family-centered care, cultural context, documentation, ethics, and neuroscientific foundations of infant mental health.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '571', 'Assistive Technology', 'Adley Chan', 'Principles of assessment, selection, training, and follow-up with clients in the use of assistive technologies to enable and enhance participation in a meaningful occupation. Open only to OT majors. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '572', 'Ergonomics', 'Marissa Marchioni', 'Focus on the effects of physical design in the workplace on users\' injury rate, behavior, performance and stress levels. Intervention for repetitive motion included. Open only to OT majors. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '573', 'Hand Rehabilitation', 'Lisa Deshaies', 'Occupation-based evaluation and intervention for individuals with acute and chronic hand disorders. Topics include scar management, splinting, peripheral nerve injury, wound healing and physical agent modalities. Open only to OT majors. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '574', 'Enhancing Motor Control for Occupation', 'Remy Chu,', 'Laboratory examining approaches to assessment and remediation of motor control following upper motor neuron lesions. An occupation-based approach to Neurodevelopmental Treatment (NDT) will be emphasized. Open only to OT majors. Graded CR/NC.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '575', 'Pediatrics Through Geriatrics', 'Joan Surfus', 'A comprehensive investigation of the anatomy and physiology of normal and abnormal swallowing. Didactic and hands-on study of assessment and treatment interventions will be addressed. Open to OT majors only. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('OT', '577', 'Seminar in Occupational Therapy', 'Myka Winder', 2258025, 'Occupational therapy and the health care system. Duplicates credit in OT 505. Open only to Occupational Therapy majors. Graded CR/NC.', 2);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '578', 'Facilitating Change in Clients', 'Jesus Diaz', 'Exploration of selected therapeutic communication styles intended to facilitate change including motivational interviewing, mindfulness practices, and cognitive approaches to therapeutic intervention.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '579', 'Occupational Therapy in Adult-Neurorehabilitation', 'Allison Chu', 'Comprehensive overview of occupational therapy evaluation and intervention in adult neurorehabilitation addressing motor, visual, cognitive, emotion, and language systems. Emphasizes evidence-based interventions with hands-on application.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '583', 'Current Applications of Lifestyle Redesign', 'Camille Dieterle', 'Interactive practicum exploring the Lifestyle Redesign approach to preventing and managing chronic health conditions. Topics include therapeutic process, lifestyle module design and service delivery models.', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('OT', '599', 'Special Topics .', 'Rebecca Aldrich', 'Recent developments in occupational therapy and occupational science.', 1);
