INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '681', 'Managing Diversity in a Global Context', 'Elizabeth Swart', 1949672, 'Interdisciplinary approach to innovative practices that make the workplace more inclusive and productive.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '682', 'Spirituality, Religion, and Faith in Clinical Practice', 'Helen Land', 1666675, 'Examination of diverse spiritual and religious traditions. Spiritually-sensitive treatment approaches applied to psychological and spiritual clinical problems of individuals, couples, and families.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '684', 'Community Practice for Social Innovation', 'Wilhelmina De Castro', 'Working effectively within marginalized, underserved populations and diverse community settings on their wicked, consistent, and complex issues by providing innovative and sustainable solutions.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '687', 'Media in Social Work', 'Rafael Angulo', 1865302, 'Creation of short documentaries for social change. Techniques in media production, strategies for media outreach, and development of media literacy skills to deconstruct media messages.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '688', 'School Violence', 'Ron Astor', 1447271, 'Examines theoretical, empirical and practice-based literature on school violence including how students\' physical well-being, academic functioning, social relations, and emotional and cognitive development are affected.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '692', 'Loss, Grief and Bereavement', 'Dawn Joosten-Hagye', 'Focus on the experiences of loss, death and bereavement as it is viewed by individuals, families and loved ones.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '694', 'Group Psychotherapy in Mental Health Settings', 'Doni Whitsett', 2230042, 'Focus on group therapy for clinical social workers as practiced in various mental health settings. The entire process of group development is examined.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '696', 'LGBT Psycho/Social/Political Issues', 'William Feuerborn', 'Overview of clinical, social and political issues with which social workers should be familiar when working with lesbian, gay, bisexual and transgender clients.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '698a', 'Integrative Learning for Advanced Social Work Practice', 'Laura Cardinal', 1988933, 'Advanced integrative learning that incorporates field experiences, evidenced-based interventions, case vignettes and dialogical inquiry through a Problem Based Learning framework.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '698b', 'Integrative Learning for Advanced Social Work Practice', 'Silvia Hernandez-Paez', 'Advanced integrative learning builds on 698A by incorporating field experiences, case/best practices consultations, and dialogical inquiry on micro, mezzo, and macro practice levels. Concurrent enrollment: SOWK 699b. Graded IP/CR/NC.', 1);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '699a', 'Advanced Applied Learning in Field Education', 'Laura Cardinal', 1988933, 'Advanced supervised field education where students practice social work skills and apply evidence-based interventions, including some that are specific to their academic department. Prerequisite: SOWK 588, SOWK 589ab.Concurrent enrollment SOWK 698a. Graded IP/CR/NC. (Duplicates credit in SOWK 686a)', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '699b', 'Advanced Applied Learning in Field Education', 'Silvia Hernandez-Paez', 'Advanced supervised field education where students practice social work skills and apply evidence-based interventions, including some that are specific to their academic department. Concurrent enrollment SOWK 698b. Graded CR/NC. (Duplicates credit in SOWK 686b).', 4);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '704', 'Strategic Innovations for the Grand Challenges', 'Cassandra Fatouros', 'Grand Challenge initiatives and interventions from diverse perspectives are explored. Multidisciplinary strategies of innovation are analyzed to effect broad-based opportunities and barriers to change.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '705', 'Leading Public Discourse', 'Charles Lewis Jr ', 'Leading public discourse for the purposes of increasing civic engagement and public participation, building broad based public support and enabling competencies.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '706', 'Leading and Managing Large Complex Systems', 'Michael Rank', 2401069, 'Examining large scale national, state and local social intervention programs as well as implications for fiscal and outcome accountability.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '707', 'Financial Management for Social Change', 'Cassandra Fatouros', 'Effective financial management in human service organizations emphasizing designing fiscal approaches that maximize revenue, improve decision making, and support disruptive social change.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '710', 'Preparatory Scholarship for Capstone', 'Ronald Manderscheid', 'Design and develop an innovative, feasible, logical, and defensible Grand Challenge Capstone Project; consider contextual issues, and enhance innovation and communication skills', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '711', 'Design Laboratory for Social Innovation I', 'Nadia Islam', 1735956, 'Integrates design thinking with innovation dynamics as a practice methodology for social innovation with application towards the development of an innovation corresponding to capstone project.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '713', 'The Application of Implementation Science', 'Jeffrey Yarvis', 2163896, 'Introduction to doctoral level research and evaluation, introducing concepts of effectively using literature, formulating a problem for research, and effectively identifying a research or evaluative approach.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, instructorID, info, units) VALUES ('SOWK', '720', 'Communication and Influence for Social Good', 'Michael Rank', 2401069, 'Explores how communication and new information technology influence decision-makers and the general public to address the Grand Challenges in the interest of the public good.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '721', 'Data Driven Decision Making in Social Services', 'Loc Nguyen', 'Practical introduction to using quantitative administrative data to identify trends, track performance, evaluate programs, and drive decision making.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '722', 'Implementing Your Capstone and Re-envisioning Your Career', 'Marvin Southard', 'Culminates in the completion of a fully implementable Grand Challenge capstone project and a plan that re-envisions career pathways conducive to launching the project.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '723', 'Design Laboratory for Social Innovation II', 'Stephanie Wander', 'The Design Laboratory provides a space for DSW students to test theoretical frameworks and models for their Grand Challenge Capstone Project.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '743', 'Theories for Practice with Small Systems', 'Daniel Hackman', 'Early practice theories and their historical roots are examined. Implications for evolving current practice theories with individuals, families, and groups are discussed.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '744', 'Theories for Practice with Large Systems', 'Monica Jolles', 'Examination of the development and utility of theories, models and approaches to social work community and administrative practice.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '761L', 'Multiple Regression for Social Work Research', 'Jungeun Olivia Lee,', 'Multivariate statistical methods including descriptive and inferential statistics, parametric and non-parametric tests of hypotheses; correlation, analysis of variance, multiple regression, and factor analysis; utilization of computer programs for statistical analysis.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '763', 'Issues in Research for Social Work Practice', 'Jordan Davis', 'Research methods to provide students with advanced methodological knowledge in two areas related to social work practice: psychotherapy outcome research, and program research.', 3);
INSERT INTO DegreeClass (degreeName, classNumber, className, instructorName, info, units) VALUES ('SOWK', '765', 'Writing Successful Dissertation Research Grants', 'Suzanne Wenzel', 'Prepares doctoral students to write and submit applications to obtain extramural funding to support their dissertation research. Students will learn about opportunities and eligibility requirements for federal and foundation supported dissertation research grants, how grants are reviewed and scored once submitted, and how to write successful proposals. Recommended preparation: Completion of a qualitative or mixed methods course is preferred.', 3);